import React from 'react';
import { Code, MonitorSmartphone, CloudCog, Lightbulb, Rocket, PieChart } from 'lucide-react';

export default function Skills() {
  const services = [
    {
      title: "Software Development",
      description: "Custom software solutions tailored to the specific needs of hospitality businesses.",
      icon: <Code className="h-10 w-10 text-primary" />
    },
    {
      title: "Mobile Applications",
      description: "Native and cross-platform mobile apps for enhanced guest experiences.",
      icon: <MonitorSmartphone className="h-10 w-10 text-primary" />
    },
    {
      title: "Cloud Solutions",
      description: "Scalable cloud infrastructure for efficient operations and data management.",
      icon: <CloudCog className="h-10 w-10 text-primary" />
    },
    {
      title: "Digital Marketing",
      description: "Strategic digital marketing services to boost your online presence and reach.",
      icon: <Lightbulb className="h-10 w-10 text-primary" />
    },
    {
      title: "Payment Gateway Integration",
      description: "Secure and seamless payment processing solutions for your business.",
      icon: <Rocket className="h-10 w-10 text-primary" />
    },
    {
      title: "Business Analytics",
      description: "Data-driven insights to help you make informed business decisions.",
      icon: <PieChart className="h-10 w-10 text-primary" />
    }
  ];

  return (
    <section id="skills" className="py-24">
      <div className="container px-4 mx-auto">
        <div className="text-center max-w-3xl mx-auto mb-16">
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            Our <span className="bg-gradient-to-r from-primary to-primary/60 bg-clip-text text-transparent">Services</span>
          </h2>
          <p className="text-muted-foreground">
            We offer a comprehensive range of technology services designed specifically for the hospitality and tourism industry in Nepal.
          </p>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div 
              key={index} 
              className="bg-card hover:shadow-lg transition-shadow rounded-xl p-8 border border-border"
            >
              <div className="mb-6">
                {service.icon}
              </div>
              <h3 className="text-xl font-semibold mb-3">{service.title}</h3>
              <p className="text-muted-foreground">{service.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
